class Type {
    constructor(el, options) {
    this.el = document.querySelector(el);
    this.strings = options.strings;
    this.typeSpeed = options.typeSpeed || 100;
    this.backSpeed = options.backSpeed || 50;
    this.backDelay = options.backDelay || 1500;
    this.loop = options.loop || false;
    this.txt = '';
    this.wordIndex = 0;
    this.isDeleting = false;
    this.type();
    }

    type() {
    const current = this.wordIndex % this.strings.length;
    const fullTxt = this.strings[current];

    if (this.isDeleting) {
        this.txt = fullTxt.substring(0, this.txt.length - 1);
    } else {
        this.txt = fullTxt.substring(0, this.txt.length + 1);
    }

    this.el.innerHTML = `<span>${this.txt}</span>`;

    let speed = this.isDeleting ? this.backSpeed : this.typeSpeed;

    if (!this.isDeleting && this.txt === fullTxt) {
        speed = this.backDelay;
        this.isDeleting = true;
    } else if (this.isDeleting && this.txt === '') {
        this.isDeleting = false;
        this.wordIndex++;
        speed = this.typeSpeed;
    }

    setTimeout(() => this.type(), speed);
    }
}
